package com.poc.livraria;

import static org.hamcrest.CoreMatchers.not;
import static org.hamcrest.MatcherAssert.assertThat;
import static org.junit.Assert.assertEquals;
import static org.junit.Assert.fail;

import java.math.BigDecimal;
import java.time.LocalDate;
import java.util.List;

import org.hamcrest.collection.IsEmptyCollection;
import org.hamcrest.collection.IsIterableContainingInOrder;
import org.junit.FixMethodOrder;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.MethodSorters;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.dao.EmptyResultDataAccessException;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.data.domain.Sort;
import org.springframework.test.context.junit4.SpringRunner;

import com.poc.livraria.domain.Livro;
import com.poc.livraria.domain.LivroService;
import com.poc.livraria.infrastructure.repository.LivroRepository;
import com.poc.livraria.infrastructure.repository.filter.LivroFilter;

@RunWith(SpringRunner.class)
@SpringBootTest
@FixMethodOrder(MethodSorters.NAME_ASCENDING)
public class LivroRepositoryTest {
	
	Livro livroAngular = new Livro(null, "9788533302280", "William Pereira Alves",
			"Desenvolvimento de Aplicações Web com Angular 6", new BigDecimal("40.00"), LocalDate.of(2010, 01, 01),
			"data:image/jpeg;base64,");
	Livro livroAngular2 = new Livro(6l, "9788533302280", "William Pereira Alves",
			"Desenvolvimento de Aplicações Web com Angular 6 - NOVA EDIÇÃO", new BigDecimal("80.00"), LocalDate.of(2010, 01, 01),
			"data:image/jpeg;base64,");
	
	Livro livroEvans1 = new Livro(1L, "0321125215", "Eric Evans", "Domain Driven Design", new BigDecimal(262.26), LocalDate.of(2003, 8, 19), "data:image/jpeg;base64,");
	Livro livroEvans2 = new Livro(5L, "1457501198", "Eric Evans", "Domain-Driven Design Reference: Definitions and Pattern Summaries", new BigDecimal(91.9), LocalDate.of(2016, 7, 11), "data:image/jpeg;base64,\"");

	
	@Autowired
	private LivroRepository livroRepository;

	@Autowired
	private LivroService livroService;

	
	
	@Test
	public void test1obterTodosOsLivros() {
		List<Livro> livrosEncontrados = livroRepository.findAll();
		assertThat(livrosEncontrados, not(IsEmptyCollection.empty()));

	}
	@Test
	public void test2PesquisarLivrosPeloAutorOrdenadoPeloNomeDoLivro() {
		LivroFilter livroFilter = new LivroFilter();
		livroFilter.setAutor("Evans");
		Sort sort = new Sort(Sort.Direction.ASC, "nome");
		Pageable pageable = new PageRequest(0, 10, sort);
		Page<Livro> livrosEncontradosPage = livroRepository.filtrar(livroFilter, pageable);
		assertThat(livrosEncontradosPage.getContent(), IsIterableContainingInOrder.contains(livroEvans1, livroEvans2));
	}

	@Test
	public void test3CriarLivro() {
		Livro livrosSalvo = livroRepository.save(livroAngular);
		assertEquals(livrosSalvo, livroAngular);

	}

	@Test
	public void test4AtualizarLivro() {
		Livro livrosSalvo = livroService.atualizar(6l, livroAngular2);
		assertEquals(livrosSalvo, livroAngular2);

	}

	@Test
	public void test5RemoverLivro() {
		try {
			livroRepository.delete(6l);
		} catch (EmptyResultDataAccessException ex) {
			fail("Não existe livro com o código informado");
		}
	}

}